CREATE TABLE app_user (id INT AUTO_INCREMENT, name VARCHAR(100), email VARCHAR(200) NOT NULL, photo VARCHAR(50), salt VARCHAR(100), password VARCHAR(100), recover_token VARCHAR(100), enabled TINYINT(1) DEFAULT '1', last_access datetime, company_id INT NOT NULL, user_role_id INT NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX company_id_idx (company_id), INDEX user_role_id_idx (user_role_id), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_general_ci ENGINE = INNODB;
CREATE TABLE city (id INT AUTO_INCREMENT, name VARCHAR(100) NOT NULL, geo_zone_id INT NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX geo_zone_id_idx (geo_zone_id), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_general_ci ENGINE = INNODB;
CREATE TABLE company (id INT AUTO_INCREMENT, name VARCHAR(50) NOT NULL, email VARCHAR(200), address VARCHAR(100), phone VARCHAR(50), logo VARCHAR(50), created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_general_ci ENGINE = INNODB;
CREATE TABLE currency (id INT AUTO_INCREMENT, name VARCHAR(50) NOT NULL, iso_code VARCHAR(5), symbol VARCHAR(5), created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_general_ci ENGINE = INNODB;
CREATE TABLE gallery (id INT AUTO_INCREMENT, former_name VARCHAR(150) NOT NULL, internal_name VARCHAR(150) NOT NULL, real_property_id INT NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX real_property_id_idx (real_property_id), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_general_ci ENGINE = INNODB;
CREATE TABLE geo_zone (id INT AUTO_INCREMENT, name VARCHAR(100) NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_general_ci ENGINE = INNODB;
CREATE TABLE google_map (id INT AUTO_INCREMENT, map_content text, real_property_id INT NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX real_property_id_idx (real_property_id), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_general_ci ENGINE = INNODB;
CREATE TABLE neighborhood (id INT AUTO_INCREMENT, name VARCHAR(100) NOT NULL, city_id INT NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX city_id_idx (city_id), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_general_ci ENGINE = INNODB;
CREATE TABLE operation (id INT AUTO_INCREMENT, name VARCHAR(50) NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_general_ci ENGINE = INNODB;
CREATE TABLE operation_real_property (operation_id INT, real_property_id INT, price DECIMAL(10, 2) DEFAULT 0, currency_id INT NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX currency_id_idx (currency_id), PRIMARY KEY(operation_id, real_property_id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_general_ci ENGINE = INNODB;
CREATE TABLE property_type (id INT AUTO_INCREMENT, name VARCHAR(100) NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_general_ci ENGINE = INNODB;
CREATE TABLE real_property_translation (id INT, detail text, lang CHAR(2), PRIMARY KEY(id, lang)) DEFAULT CHARACTER SET utf8 COLLATE utf8_general_ci ENGINE = INNODB;
CREATE TABLE real_property (id INT AUTO_INCREMENT, name VARCHAR(250) NOT NULL, youtube text, status VARCHAR(30), property_type_id INT NOT NULL, neighborhood_id INT NOT NULL, app_user_id INT NOT NULL, INDEX property_type_id_idx (property_type_id), INDEX neighborhood_id_idx (neighborhood_id), INDEX app_user_id_idx (app_user_id), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_general_ci ENGINE = INNODB;
CREATE TABLE search_match (search_profile_id INT, real_property_id INT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(search_profile_id, real_property_id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_general_ci ENGINE = INNODB;
CREATE TABLE search_profile (id INT AUTO_INCREMENT, property_type_id INT, operation_id INT, geo_zone_id INT, city_id INT, neighborhood_id INT, app_user_id INT, min_price DECIMAL(10, 2) DEFAULT 0, max_price DECIMAL(10, 2) DEFAULT 0, is_vendor TINYINT(1) DEFAULT '0', reference VARCHAR(150), created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX property_type_id_idx (property_type_id), INDEX operation_id_idx (operation_id), INDEX geo_zone_id_idx (geo_zone_id), INDEX city_id_idx (city_id), INDEX neighborhood_id_idx (neighborhood_id), INDEX app_user_id_idx (app_user_id), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_general_ci ENGINE = INNODB;
CREATE TABLE user_role (id INT AUTO_INCREMENT, name VARCHAR(50) NOT NULL, credentials VARCHAR(250), code VARCHAR(50), created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_general_ci ENGINE = INNODB;
CREATE TABLE vendor_zone (app_user_id INT, neighborhood_id INT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(app_user_id, neighborhood_id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_general_ci ENGINE = INNODB;
CREATE TABLE video (id INT AUTO_INCREMENT, youtube text, uploaded VARCHAR(50), real_property_id INT NOT NULL, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, INDEX real_property_id_idx (real_property_id), PRIMARY KEY(id)) DEFAULT CHARACTER SET utf8 COLLATE utf8_general_ci ENGINE = INNODB;
ALTER TABLE app_user ADD CONSTRAINT app_user_user_role_id_user_role_id FOREIGN KEY (user_role_id) REFERENCES user_role(id) ON DELETE CASCADE;
